import { CheckBox } from "./CheckBox";
import { productsApi } from "../../../store/servises";
import { useContext } from "react";
import { ProductsContext } from "../../../App";



export const Categories = ({active, setActive}) => {
    const{ state, setState} = useContext(ProductsContext)
    const { data: categories } = productsApi.useGetCategoriesQuery();

const handleClick = (i) =>{
    setState({ ...state, search: " ", category: i})
}

  return (
    <div className="categories">
      <h3> Категории: </h3>
<button>  Категории: </button>

      {state.category !== null ? (
        <button onClick={() => setState({...state, category: null})} className="outline">
          clear
        </button>


      ) : (
        " "
      )}



      <ul>
        {categories &&
          categories.map((c, i) => (
            <li key={c} onClick={() => handleClick(i)}>
              <CheckBox
                checked={i === state.category}
                name="category"
                value={c[0].toUpperCase() + c.slice(1)}
                round="round"
              />
            </li>
          ))}
      </ul>
    </div>
  );
};
