import { ReactComponent as Logo } from "./../../../utils/next.svg";



export const Pagination = ({ pages,currentPage, setCurrentPage, refetch}) => {


    const changePage = (p)=>{
        setCurrentPage(p)
        refetch()
    }
    let res = []
    if (currentPage === 1 || currentPage === 2 ){
        res=pages.filter((el,i,arr) => el<=3 || el === arr[arr.length])
    }


  return (
    <div className="pagination">
      <Logo className="prev outline button_pagination" />

      {
        res.map((p) => (
          <button className={[ "button_pagination", currentPage === p ? "primary" : "outline"].join(" ")} key={p}  onClick={()=>changePage(p)}>
            {p}
          </button>
        ))}

      <Logo className="next outline button_pagination" />
    </div>
  );
};
