import { CheckBox } from "./CheckBox";
import { productsApi } from "../../../store/servises";
import { useState } from "react";

export const Categories = () => {
  const { data: categories } = productsApi.useGetCategoriesQuery();
  const [active, setActive] = useState(null);

  return (
    <div className="categories">
      <h3> Категории: </h3>
    {active !== null  ? <button onClick={()=> setActive(null)}> clear </button>  : " " }
      <ul>
        {categories &&
          categories.map((c, i) => (
            <li key={c} onClick={() => setActive(i)}>
              <CheckBox
                checked={i === active}
                name="category"
                value={c[0].toUpperCase() + c.slice(1)}
                round="round"
              />
            </li>
          ))}
      </ul>
    </div>
  );
};
