import { ReactComponent as Logo } from "./../../../utils/next.svg";
import { ReactComponent as Dots } from "./../../../utils/dots.svg";

export const Pagination = ({ pages, currentPage, setCurrentPage, refetch }) => {
  console.log(currentPage);
  console.log(pages.length);

  const first = currentPage === 1;
  const oneTwo = currentPage <= 3;
  const threeSeven = currentPage > 3 && currentPage < pages[pages.length - 1];
  const threeLast = currentPage > pages[pages.length - 3];
  const notFirst3 = threeSeven || threeLast;

  const changePage = (p) => {
    setCurrentPage(p);
    refetch();
  };
  let res = [];

  if (oneTwo) {
    res = pages.filter((el, i, arr) => el <= 4);
  }
  if (threeSeven) {
    res = pages.filter(
      (el, i, arr) =>
        el === currentPage - 1 || el === currentPage || el === currentPage + 1
    );
  }
  if (threeLast) {
    res = pages.filter((el, i, arr) => el > pages[pages.length - 4]);
  }

  return (
    <div className="pagination">
      <Logo className="prev outline button_pagination" />

      {/* первая кнопка */}
      {notFirst3 && (
        <button
          className={[
            "button_pagination",
            currentPage === pages[0] ? "primary" : "outline",
          ].join(" ")}
          onClick={() => changePage(pages[0])}>
          {pages[0]}
        </button>
      )}

      {notFirst3 && <Dots className="next outline button_pagination" />}

      {/* куча кнопок */}
      {res.map((p) => (
        <button
          className={[
            "button_pagination",
            currentPage === p ? "primary" : "outline",
          ].join(" ")}
          key={p}
          onClick={() => changePage(p)}>
          {p}
        </button>
      ))}

      {/* точки */}
      {!threeLast && <Dots className="next outline button_pagination" />}


      {!threeLast && (
        <button
          className={[
            "button_pagination",
            currentPage === pages[pages.length - 1] ? "primary" : "outline",
          ].join(" ")}
          onClick={() => changePage(pages[pages.length - 1])}>
          {pages[pages.length - 1]}
        </button>
      )}

      <Logo className="next outline button_pagination" />
    </div>
  );
};
