import { CheckBox } from "./CheckBox"
import { productsApi} from "../../../store/servises";
import { useState } from "react";



export const SideBar=()=>{
    const {  data: categories } = productsApi.useGetCategoriesQuery()
    const [active, setActive] = useState(false);
    return(
        <div className="sidebar">
            <CheckBox value="Есть на складе"/>
            <h3> Категории: </h3>

            <ul>
                { categories && categories.map((c,i)=>  <li key={c}> <CheckBox  onClick={() => setActive(i)} checked={i === active} name="category" value={c[0].toUpperCase() + c.slice(1)} round="round"/></li>)}
            </ul>

        </div>
    )
}
