import { useState, useEffect, useContext } from "react";
import { ProductsContext } from "../../../App";
import { getArray } from "../../../hooks/usePagination";
import { productsApi } from "../../../store/servises";
import { Pagination } from "./Pagination";
import { ProductItem } from "./ProductItem";

export const Products = ({ active }) => {
  let count = 5;
  let pages = [];
  const {state, setState} = useContext(ProductsContext)

  const [currentPage, setCurrentPage] = useState(1);
  const skip = count * (currentPage - 1);
  const { data: categories } = productsApi.useGetCategoriesQuery();

let prodWithSort = productsApi.useGetProductsQuery({ count, skip }, {
  selectFromResult: ({ data }) => ({
    data: data?.filter((el) => el.title === state.search),
  }),
})




  const { data, refetch } = 
     categories && active !== null
     ? productsApi.useGetProductsOfCategoriesQuery({ category: categories[active]})
     : productsApi.useGetProductsQuery({ count, skip })

  if (data) {
    pages = getArray(data.total / count);
  }

  return (
    <div>
      {data &&
        data.products.map((p) => (
          <ProductItem
            title={p.title}
            img={p.images[0]}
            price={p.price}
            descr={p.description}
            count={p.stock}
            key={p.id}
          />
        ))}
      {pages.length>6 && (
        <Pagination
          pages={pages}
          currentPage={currentPage}
          setCurrentPage={setCurrentPage}
          refetch={refetch}
        />
      )}
    </div>
  );
};
